package com.example.proyectoZapateria.data.local.region

import androidx.room.ColumnInfo
import androidx.room.Entity
import androidx.room.Index
import androidx.room.PrimaryKey

/**
 * Nota: Esta entidad está marcada como obsoleta.
 * La geografía (regiones/ciudades/comunas) ahora se obtiene desde el microservicio remoto
 * y las tablas locales se eliminaron del esquema de Room en `AppDatabase`.
 * Conservamos la clase por compatibilidad temporal; puede ser eliminada en una limpieza futura.
 */
@Deprecated("Reemplazado por geografía remota - mantener temporalmente para compatibilidad")
@Suppress("unused")
@Entity(
    tableName = "region",
    indices = [Index(value = ["nombre_region"], unique = true)]
)
data class RegionEntity(
    @PrimaryKey(autoGenerate = true)
    @ColumnInfo(name = "id_region")
    val idRegion: Int = 0,

    @ColumnInfo(name = "nombre_region")
    val nombreRegion: String,

    val abreviatura: String?
)

